const MenuApp: React.FC = () => {
  return (
    <Menu style={{ minHeight: 700, backgroundColor: "#f5f5f5" }} mode="inline">
      <Image
        width={100}
        style={{ right: 20, paddingBottom: 15 }}
        src={`${process.env.PUBLIC_URL}/img/insight-05 1.png`}
      />

      {items.map((item) =>
        item.children ? (
          <SubMenu
            key={item.key}
            icon={item.icon}
            title={item.label}
            className="custom-submenu"
          >
            {item.children.map((child) => (
              <Menu.Item key={child.key}>
                <Link to={child.path}>{child.label}</Link>
              </Menu.Item>
            ))}
          </SubMenu>
        ) : (
          <Menu.Item key={item.key} icon={item.icon}>
            <Link to={item.path}>{item.label}</Link>
          </Menu.Item>
        )
      )}
    </Menu>
  );
};

export default MenuApp;
